source:
mixin section-textmedia(alignment, options)
  - var cls = [];
  - cls.push('section--textmedia');

  if alignment === ALIGNMENT_RIGHT
    - cls.push('section--textmedia-right');

  if alignment === ALIGNMENT_LEFT
    - cls.push('section--textmedia-left');

  .section(class=cls)
    .section-wrapper
      .section__content
        +textmedia(options)
          block

